<?php

namespace Tsf\Dao;

/**
 * Test class for ImageDao.
 * Generated by PHPUnit on 2012-08-15 at 22:52:25.
 */
class ImageDaoTest extends \CommonDbTestCase
{

    /**
     * @var ImageDao
     */
    protected $dao;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp()
    {
        parent::setUp();
        $this->dao = new ImageDao($this->db);
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown()
    {
        $this->dao = null;
        parent::tearDown();
    }
    
    public function testConstruct()
    {
        $this->assertInstanceOf('\Tsf\Dao\ImageDao', $this->dao);
    }

    /**
     * @covers Tsf\Dao\ImageDao::find
     */
    public function testFind()
    {
        $result = $this->dao->find(1);
        $this->assertInternalType('array', $result);
        $this->assertEquals(1, $result['day']);
        $this->assertEquals(7606616668, $result['photo_id']);
    }

    /**
     * @covers Tsf\Dao\ImageDao::findAll
     */
    public function testFindAll()
    {
        $result = $this->dao->findAll();
        $this->assertInternalType('array', $result);
        $this->assertEquals(7, count($result));
    }

    /**
     * @covers Tsf\Dao\ImageDao::save
     */
    public function testSave()
    {
        $result = $this->dao->save(array('day' => 200, 'photo_id' => 999999));
        $this->assertEquals(1, $result);

        $image = $this->dao->find(200);
        $this->assertEquals(200, $image['day']);
    }

    /**
     * @covers Tsf\Dao\ImageDao::delete
     */
    public function testDelete()
    {
        $this->assertEquals(7, count($this->dao->findAll()));
        $this->assertEquals(1, $this->dao->delete(1));
        $this->assertFalse($this->dao->find(1));
        $this->assertEquals(6, count($this->dao->findAll()));
    }

}